export function post(endpoint, data) {
	return fetch(endpoint, {
		method: 'POST',
		credentials: 'include',
		body: JSON.stringify(data),
		headers: {
			'Content-Type': 'application/json'
		}
	}).then((response) => {
		return response.json().then((data) => {
			return {
				data,
				status:  response.status
			}
		})
	})
	.catch(error => {
		console.log(error);
		return {
			error
		}
	});
}
